<?php

namespace AppBundle\Repository;

/**
 * ProviderRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ProviderRepository extends \Doctrine\ORM\EntityRepository
{
    public function findProvider($name, $postCode, $categorie){

        $qb = $this->createQueryBuilder('p');

        if($name != '')
        {
            $qb->andWhere('p.name LIKE :name')
                ->setParameter('name','%'.$name.'%');
        }
        if($postCode != '')
        {
            $qb
                ->leftJoin('p.postCode','pc')
                ->addSelect('pc')
                ->andWhere('pc.postCode = :postCode')
                ->setParameter('postCode',$postCode);
        }
        if($categorie != '')
        {
            $qb
                ->leftJoin('p.serviceCategories', 'sc','WITH','sc.name = :serviceCategories')
                ->setParameter('serviceCategories',$categorie)
//                ->leftJoin('p.serviceCategories', 'sc')
//                ->addSelect('sc')
//                ->andWhere('sc.name = :serviceCategories')
//                ->setParameter('serviceCategories', $categorie)
            ;
        }

        return $qb
            ->getQuery()
            ->getResult()
            ;
    }
